import React, { Suspense }  from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import './index.css';
import App from './App';

import store from './redux/store'
import {Provider} from 'react-redux'

// ReactDOM.render(
