// 	<Provider store={store}>
//     <App/>
//   </Provider>,
//   document.getElementById('root')
// );

ReactDOM.render(
  <BrowserRouter>
    {/* 使用了路由懒加载，所以需要使用<Suspense>包起来 */}
    <Suspense fallback={<div></div>}>
      <Routes>
        <Route path="/" render={routerProps => {
          return <App {...routerProps}/>
        }}/>
      </Routes>
    </Suspense>
  </BrowserRouter>,
  document.getElementById('root')
)
